-1.152 5.148.1-.477-.836-3.765 1.152-5.148m12.144-.096c-.358-.156-3.505 2.176-1.307 5.2-.168-.553-.719-4.232 1.307-5.2M9.75 18.858l2.653 12.572 2.755 5.045 1.086 6.177.703.225-1.086-6.452-2.804-4.97zm29.72-6.74c-.36-.157-3.474 2.265-1.293 5.245-.21-.984-1.033-4.355 1.292-5.246" opacity=".25" fill="#fff"/></g></svg>';

    #piece(@piece, @type) {
      @raw: if(
        @type = "white",
        replace(
          replace(
            replace(
              replace(@piece, "%BORDER%", @mantle),
              "%BG%",
              @white-piece-bg
            ),
            "#fff",
            @mantle
          ),
          "#000",
          @text
        ),
        replace(
          replace(
            replace(
              replace(@piece, "%BORDER%", @mantle),
              "%BG%",
              @black-piece-bg
            ),
            "#fff",
            @text
          ),
          "#000",
          @mantle
        )
      );
      @svg: escape(@raw);
      @result: url("data:image/svg+xml,@{svg}");
    }

    .board-popover-component {
      background-color: @surface0;
    }

    & when (@styleBoardAndPieces = 1) {
      --theme-board-style-image: url("data:image/svg+xml,@{board}");
      --theme-board-style-highlight-color: @highlight1;

      --theme-piece-set-wp: #piece(@pawn, "white")[];
      --theme-piece-set-wn: #piece(@knight, "white")[];
      --theme-piece-set-wr: #piece(@rook, "white")[];
      --theme-piece-set-wb: #piece(@bishop, "white")[];
      --theme-piece-set-wq: #piece(@queen, "white")[];
      --theme-piece-set-wk: #piece(@king, "white")[];
      --theme-piece-set-bp: #piece(@pawn, "black")[];
      --theme-piece-set-bn: #piece(@knight, "black")[];
      --theme-piece-set-bb: #piece(@bishop, "black")[];
      --theme-piece-set-br: #piece(@rook, "black")[];
      --theme-piece-set-bq: #piece(@queen, "black")[];
      --theme-piece-set-bk: #piece(@king, "black")[];

      .piece {
        /* Black */
        &.bp {
          background-image: #piece(@pawn, "black")[] !important;
        }
        &.bn {
          background-image: #piece(@knight, "black")[] !important;
        }
        &.bb {
          background-image: #piece(@bishop, "black")[] !important;
        }
        &.br {
          background-image: #piece(@rook, "black")[] !important;
        }
        &.bk {
          background-image: #piece(@king, "black")[] !important;
        }
        &.bq {
          background-image: #piece(@queen, "black")[] !important;
        }

        /* White */
        &.wp {
          background-image: #piece(@pawn, "white")[] !important;
        }
        &.wn {
          background-image: #piece(@knight, "white")[] !important;
        }
        &.wb {
          background-image: #piece(@bishop, "white")[] !important;
        }
        &.wr {
          background-image: #piece(@rook, "white")[] !important;
        }
        &.wk {
          background-image: #piece(@king, "white")[] !important;
        }
        &.wq {
          background-image: #piece(@queen, "white")[] !important;
        }
      }
    }

    .chessboard-pkg-move-list-component .dark-row {
      background-color: @surface1;
    }
  }
}

/* ==UserStyle==
@name Lichess Catppuccin
@namespace github.com/catppuccin/userstyles/styles/lichess
@homepageURL https://github.com/catppuccin/userstyles/tree/main/styles/lichess
@version 2025.09.19
@updateURL https://github.com/catppuccin/userstyles/raw/main/styles/lichess/catppuccin.user.less
@supportURL https://github.com/catppuccin/userstyles/issues?q=is%3Aopen+is%3Aissue+label%3Alichess
@description Soothing pastel theme for Lichess
@author Catppuccin
@license MIT

@preprocessor less
@var select lightFlavor "Light Flavor" ["latte:Latte*", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha"]
@var select darkFlavor "Dark Flavor" ["latte:Latte", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha*"]
@var select accentColor "Accent" ["rosewater:Rosewater", "flamingo:Flamingo", "pink:Pink", "mauve:Mauve*", "red:Red", "maroon:Maroon", "peach:Peach", "yellow:Yellow", "green:Green", "teal:Teal", "blue:Blue", "sapphire:Sapphire", "sky:Sky", "lavender:Lavender", "subtext0:Gray"]

@var select lastMoveColor "Last Move" ["rosewater:Rosewater", "flamingo:Flamingo", "pink:Pink", "mauve:Mauve", "red:Red*", "maroon:Maroon", "peach:Peach", "yellow:Yellow", "green:Green", "teal:Teal", "blue:Blue", "sapphire:Sapphire", "sky:Sky", "lavender:Lavender", "subtext0:Gray"]
@var select checkColor "Check Indicator" ["rosewater:Rosewater", "flamingo:Flamingo", "pink:Pink", "mauve:Mauve", "red:Red*", "maroon:Maroon", "peach:Peach", "yellow:Yellow", "green:Green", "teal:Teal", "blue:Blue", "sapphire:Sapphire", "sky:Sky", "lavender:Lavender", "subtext0:Gray"]
@var checkbox stylePieces "Style Pieces" 1
@var checkbox styleBoard "Style Board" 1
==/UserStyle== */

@import "https://userstyles.catppuccin.com/lib/lib.less";

@-moz-document domain("lichess.org") {
  :root:has(body[data-theme="light"]) {
    #catppuccin(@lightFlavor);
  }
  :root:has(body[data-theme="dark"]) {
    #catppuccin(@darkFlavor);
  }
  :root:has(body[data-theme="system"]) {
    @media (prefers-color-scheme: light) {
      #catppuccin(@lightFlavor);
    }
    @media (prefers-color-scheme: dark) {
      #catppuccin(@darkFlavor);
    }
  }

  #catppuccin(@flavor) {
    #lib.palette();
    #lib.defaults();

    @lastMove: @catppuccin[@@flavor][@@lastMoveColor];
    @check: @catppuccin[@@flavor][@@checkColor];

    &,
    body {
      background: @base linear-gradient(to bottom, @crust, @base 116px)
        no-repeat;
      color: @text !important;
    }

    /* Button */
    .site-title a {
      color: @accent;
    }
    .site-title span {
      color: @text;
    }

    /* Game selection menus */
    .hooks__list td {
      background-color: fade(@overlay0, 50%);
    }
    .hooks__list tr.join:hover td {
      background-color: fade(@accent, 50%);
    }

    /* Topbar */
    #topnav.hover section:hover > a,
    #topnav section:active > a,
    #topnav div,
    .site-buttons .shown .toggle,
    .site-buttons .dropdown {
      background: @crust;
    }
    #topnav section > a {
      color: @subtext0;
    }
    #topnav.hover section:hover > a,
    #topnav section:active > a,
    #topnav div a,
    #dasher_app .selector button,
    #dasher_app .head,
    #dasher_app .piece .no-square,
    #dasher_app .theme .list button,
    #dasher_app .links a,
    #dasher_app .links button,
    #dasher_app .subs .sub,
    .site-buttons .dropdown a,
    .site-buttons .dropdown button {
      color: @text;
    }
    #topnav.hover section:hover > a,
    #topnav section:active > a,
    #topnav div {
      border-left-color: @accent;
    }
    signal > i {
      background-color: @green;
    }
    #dasher_app .sub::before,
    .is-green::before {
      color: @green;
    }
    #challenge-app .empty {
      background: @crust;
    }

    /* Popup skining */
    dialog {
      background: @crust;
    }
    .game-setup .optional-config,
    .game-setup .ratings {
      background: @base;
    }
    .game-setup .radio input:checked + label {
      background: @green;
    }

    /* Text */
    .site-buttons .link,
    .button,
    .input,
    .optgroup,
    .select,
    .textarea,
    .lobby__box td.name a,
    .lobby__box__top .title,
    .lobby__box .user-link,
    .mini-game,
    .mini-game:hover,
    .text,
    .ublog-post-card__title,
    .lobby__counters a,
    .lobby__streams .stream,
    .lobby__timeline .entry a,
    .lobby__about a,
    .lobby__timeline,
    .lobby__counters,
    .tabs-horiz,
    .lobby__streams .more,
    .lobby__timeline .more,
    .dialog-content,
    button,
    input,
    optgroup,
    select,
    textarea {
      color: @subtext0;
    }
    .site-buttons .link:hover,
    .button.button-metal:hover,
    .radio input:checked + label {
      color: @text;
    }
    .site-title-nav__donate,
    .lobby__streams .stream.highlight strong,
    .utitle,
    .lobby__support__text,
    .lobby__support i::before {
      color: @peach;
    }
    .mini-game__clock.clock--run,
    .tabs-horiz span.active {
      color: @red;
    }
    .tabs-horiz span.active,
    .tabs-horiz span:hover {
      border-color: @red;
    }
    a {
      color: @blue;
    }
    /* Links */
    .lobby__timeline .entry:hover a,
    a:hover,
    a:active,
    a:focus {
      color: @accent;
    }

    /* Homepage cards */
    .lobby__app__content,
    .box {
      background-color: @mantle;
    }
    .lpools > div {
      background: fade(@crust, 20%);
      border-color: @surface0;
      &:hover {
        background-color: fade(@accent, 20%);
      }
    }
    .hook__filters {
      background-color: fade(@mantle, 50%);
    }
    .lobby__support a {
      background: @crust;
    }
    .lobby__support a:hover {
      background: @peach;
    }
    .lobby__support a:hover i::before,
    .lobby__support a:hover .lobby__support__text {
      color: @mantle;
    }
    .lobby__box__top,
    #hook .opponent,
    .lobby__box__content,
    .ublog-post-card,
    .button,
    .tour-spotlight {
      background: @mantle !important;
    }
    .unread {
      background: @red;
    }
    .lobby__box tr:nth-child(even) {
      background: @crust;
    }

    /* Non-main page sidebar nav colors */
    .subnav a.active {
      color: @red;
    }
    .subnav a.active::after,
    .subnav a:hover::after {
      background: @red;
    }

    /* Blog cards */
    .blog-cards > a {
      background: @crust;
    }

    /* Learn tab */
    .learn-stages .stage.done {
      background: @green;
    }
    .learn-stages .stage.ongoing,
    .learn__side-home {
      background: @blue;
    }
    .learn-stages .stage.future {
      background: @red;
    }

    /* Chess Game */
    @light-cell: if(@flavor = latte, @surface0, @accent);
    @dark-cell: if(@flavor = latte, @accent, @surface0);
    & when (@styleBoard = 1) {
      /* Board */
      .is2d cg-board::before {
        @svg: escape(
          '<svg shape-rendering="crispEdges" viewBox="0 0 8 8" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink"><g id="f"><g id="e"><g id="d"><g id="c"><path id="a" d="M0 0h1v1H0z" fill="@{light-cell}"/><use x="1" xlink:href="#a" y="1"/><path id="b" d="M0 1h1v1H0z" fill="@{dark-cell}"/><use x="1" xlink:href="#b" y="-1"/></g><use x="2" xlink:href="#c"/></g><use x="4" xlink:href="#d"/></g><use xlink:href="#e" y="2"/></g><use xlink:href="#f" y="4"/></svg>'
        );
        background-image: url("data:image/svg+xml,@{svg}") !important;
      }

      /* File and Rank Coords */
      .orientation-white .files coord:nth-child(2n+1),
      .orientation-white .ranks coord:nth-child(2n),
      .orientation-black .files coord:nth-child(2n),
      .orientation-black .ranks coord:nth-child(2n+1) {
        color: @light-cell;
      }
      .orientation-white .files coord:nth-child(2n),
      .orientation-white .ranks coord:nth-child(2n+1),
      .orientation-black .files coord:nth-child(2n+1),
      .orientation-black .ranks coord:nth-child(2n) {
        color: @dark-cell;
      }

      /* Squares */
      square {
        &.move-dest {
          background: radial-gradient(
            @overlay1 19%,
            rgba(0, 0, 0, 0) 20%
          );
        }

        &.selected {
          background: fade(@accent, 50%);
        }

        &.last-move {
          background-color: fade(@lastMove, 75%);
        }

        &.check {
          background: radial-gradient(
            ellipse at center,
            @check 0%,
            @check 25%,
            fade(@check, 0%) 89%,
            fade(@check, 0%) 100%
          );
        }

        /* Capture indicator */
        &.oc.move-dest {
          background: radial-gradient(
            transparent 0%,
            transparent 79%,
            fade(@overlay1, 50%) 80%
          );
        }
      }

      /* Analysis Arrows */
      svg.cg-shapes g {
        &[cgHash*="green"] {
          filter: @green-filter;
        }
        &[cgHash*="paleBlue"] {
          filter: @sky-filter;
        }
        &[cgHash*="red"] {
          filter: @red-filter;
        }
      }
    }
    & when (@stylePieces = 1) {
      @w-piece-fill: if(@flavor = latte, @mantle, @text);
      @b-piece-fill: if(@flavor = latte, @text, @mantle);
      @w-piece-stroke: if(@flavor = latte, @text, @mantle);
      @b-piece-stroke: if(@flavor = latte, @mantle, @text);

      .is2d .pawn {
