      }
      .is2d .bishop {
        #piece(@f, @s) {
          @svg: escape(
            '<svg xmlns="http://www.w3.org/2000/svg" width="45" height="45"><g fill="none" fill-rule="evenodd" stroke="@{s}" stroke-width="1.5" stroke-linecap="round" stroke-linejoin="round"><g fill="@{f}" stroke-linecap="butt"><path d="M9 36c3.39-.97 10.11.43 13.5-2 3.39 2.43 10.11 1.03 13.5 2 0 0 1.65.54 3 2-.68.97-1.65.99-3 .5-3.39-.97-10.11.46-13.5-1-3.39 1.46-10.11.03-13.5 1-1.354.49-2.323.47-3-.5 1.354-1.94 3-2 3-2z"/><path d="M15 32c2.5 2.5 12.5 2.5 15 0 .5-1.5 0-2 0-2 0-2.5-2.5-4-2.5-4 5.5-1.5 6-11.5-5-15.5-11 4-10.5 14-5 15.5 0 0-2.5 1.5-2.5 4 0 0-.5.5 0 2z"/><path d="M25 8a2.5 2.5 0 1 1-5 0 2.5 2.5 0 1 1 5 0z"/></g><path d="M17.5 26h10M15 30h15m-7.5-14.5v5M20 18h5" stroke="@{s}" stroke-linejoin="miter"/></g></svg>'
          );
          background-image: url("data:image/svg+xml,@{svg}") !important;
        }
        &.black {
          #piece(@b-piece-fill, @b-piece-stroke);
        }
        &.white {
          #piece(@w-piece-fill, @w-piece-stroke);
        }
      }
      .is2d .knight {
        #piece(@f, @s) {
          @svg: escape(
            '<svg xmlns="http://www.w3.org/2000/svg" width="45" height="45"><g fill="none" fill-rule="evenodd" stroke="@{s}" stroke-width="1.5" stroke-linecap="round" stroke-linejoin="round"><path d="M22 10c10.5 1 16.5 8 16 29H15c0-9 10-6.5 8-21" fill="@{f}"/><path d="M24 18c.38 2.91-5.55 7.37-8 9-3 2-2.82 4.34-5 4-1.042-.94 1.41-3.04 0-3-1 0 .19 1.23-1 2-1 0-4.003 1-4-4 0-2 6-12 6-12s1.89-1.9 2-3.5c-.73-.994-.5-2-.5-3 1-1 3 2.5 3 2.5h2s.78-1.992 2.5-3c1 0 1 3 1 3" fill="@{f}"/><path d="M9.5 25.5a.5.5 0 1 1-1 0 .5.5 0 1 1 1 0m5.433-9.75a.5 1.5 30 1 1-.866-.5.5 1.5 30 1 1 .866.5" fill="@{f}" stroke="@{s}"/></g></svg>'
          );
          background-image: url("data:image/svg+xml,@{svg}") !important;
        }
        &.black {
          #piece(@b-piece-fill, @b-piece-stroke);
        }
        &.white {
          #piece(@w-piece-fill, @w-piece-stroke);
        }
      }
      .is2d .rook {
        #piece(@f, @s) {
          @svg: escape(
            '<svg xmlns="http://www.w3.org/2000/svg" width="45" height="45"><g fill-rule="evenodd" fill="@{f}" stroke="@{s}" stroke-width="1.5" stroke-linecap="round" stroke-linejoin="round"><path d="M9 39h27v-3H9zm3.5-7 1.5-2.5h17l1.5 2.5zm-.5 4v-4h21v4z" stroke-linecap="butt"/><path d="M14 29.5v-13h17v13z" stroke-linecap="butt" stroke-linejoin="miter"/><path d="M14 16.5 11 14h23l-3 2.5zM11 14V9h4v2h5V9h5v2h5V9h4v5z" stroke-linecap="butt"/><path d="M12 35.5h21m-20-4h19m-18-2h17m-17-13h17M11 14h23" fill="none" stroke="@{s}" stroke-width="1" stroke-linejoin="miter"/></g></svg>'
          );
          background-image: url("data:image/svg+xml,@{svg}") !important;
        }
        &.black {
          #piece(@b-piece-fill, @b-piece-stroke);
        }
        &.white {
          #piece(@w-piece-fill, @w-piece-stroke);
        }
      }
      .is2d .king {
        #piece(@f, @s) {
          @svg: escape(
            '<svg xmlns="http://www.w3.org/2000/svg" width="45" height="45"><g fill="@{f}" fill-rule="evenodd" stroke="@{s}" stroke-width="1.5" stroke-linecap="round" stroke-linejoin="round"><path d="M22.5 11.63V6" stroke-linejoin="miter"/><path d="M22.5 25s4.5-7.5 3-10.5c0 0-1-2.5-3-2.5s-3 2.5-3 2.5c-1.5 3 3 10.5 3 10.5" fill="@{f}" stroke-linecap="butt" stroke-linejoin="miter"/><path d="M11.5 37c5.5 3.5 15.5 3.5 21 0v-7s9-4.5 6-10.5c-4-6.5-13.5-3.5-16 4V27v-3.5c-3.5-7.5-13-10.5-16-4-3 6 5 10 5 10z" fill="@{f}"/><path d="M20 8h5" stroke-linejoin="miter"/><path d="M11.5 30c5.5-3 15.5-3 21 0m-21 3.5c5.5-3 15.5-3 21 0m-21 3.5c5.5-3 15.5-3 21 0" stroke="@{s}"/></g></svg>'
          );
          background-image: url("data:image/svg+xml,@{svg}") !important;
        }
        &.black {
          #piece(@b-piece-fill, @b-piece-stroke);
        }
        &.white {
          #piece(@w-piece-fill, @w-piece-stroke);
        }
      }
      .is2d .queen {
        #piece(@f, @s) {
          @svg: escape(
            '<svg xmlns="http://www.w3.org/2000/svg" width="45" height="45"><g fill="@{f}" fill-rule="evenodd" stroke="@{s}" stroke-width="1.5" stroke-linecap="round" stroke-linejoin="round"><path d="M8 12a2 2 0 1 1-4 0 2 2 0 1 1 4 0m16.5-4.5a2 2 0 1 1-4 0 2 2 0 1 1 4 0M41 12a2 2 0 1 1-4 0 2 2 0 1 1 4 0M16 8.5a2 2 0 1 1-4 0 2 2 0 1 1 4 0M33 9a2 2 0 1 1-4 0 2 2 0 1 1 4 0"/><path d="M9 26c8.5-1.5 21-1.5 27 0l2-12-7 11V11l-5.5 13.5-3-15-3 15-5.5-14V25L7 14z" stroke-linecap="butt"/><path d="M9 26c0 2 1.5 2 2.5 4 1 1.5 1 1 .5 3.5-1.5 1-1.5 2.5-1.5 2.5-1.5 1.5.5 2.5.5 2.5 6.5 1 16.5 1 23 0 0 0 1.5-1 0-2.5 0 0 .5-1.5-1-2.5-.5-2.5-.5-2 .5-3.5 1-2 2.5-2 2.5-4-8.5-1.5-18.5-1.5-27 0z" stroke-linecap="butt"/><path d="M11.5 30c3.5-1 18.5-1 22 0M12 33.5c6-1 15-1 21 0" fill="none"/></g></svg>'
          );
          background-image: url("data:image/svg+xml,@{svg}") !important;
        }
        &.black {
          #piece(@b-piece-fill, @b-piece-stroke);
        }
        &.white {
          #piece(@w-piece-fill, @w-piece-stroke);
        }
      }
    }

    .rclock .time {
      background: @mantle;
      color: @text;
    }
    .rclock.running .time {
      background: #384722; /* putting it back to default so that the time on the clock is readable */
    }
    .rclock .bar {
      background: @accent;
    }

    /* Game Cards */
    .game__meta,
    .round__app__table {
      background: @crust;
    }
    rm6 {
      background: @mantle;
    }
    rm6 .buttons,
    rm6 i5z {
      background: @base;
    }

    /* Messages */
    .msg-app__convo__head,
    .msg-app__side__search,
    .msg-app__convo__reply {
      background: @crust;
    }
    .msg-app__side {
      background: @mantle;
    }
    .msg-app__side__search input,
    .msg-app__convo__post__text {
      background: @base;
    }
    .msg-app__side__contact__name {
      color: @text;
    }
    .msg-app__side__contact__msg,
    .msg-app__side__contact__date time {
      color: @subtext0;
    }

    /* User dropdown */
    #powerTip,
    #miniGame,
    #miniBoard {
      background: @crust;
    }
    .btn-rack__btn,
    .btn-rack form,
    #friend_box .friend_box_title,
    .button.button-metal,
    .button.button-empty:not(.disabled):hover,
    .button.button-empty.button-green:not(.disabled):hover,
    .button.button-empty.button-red:not(.disabled):hover {
      background: @base;
    }

    /* User Page */
    .user-show__header,
    .user-show__social,
    .user-show .angles,
    .cmn-toggle:hover:not(:disabled) + label::after,
    .cmn-toggle + label::after,
    .crosstable povs:hover,
    .crosstable__users,
    .crosstable__score,
    .game-row:nth-child(odd),
    .number-menu--tabs .nm-item.active {
      background: @crust;
    }
    .number-menu--tabs .nm-item.active,
    .user-show .number-menu .to-games.active,
    .user-show #games.number-menu {
      background: @mantle !important;
    }
    /* Stats */
    .perf-stat .counter tr.full td:last-child {
      background: @crust;
    }
    .sub-ratings a.active {
      background: @mantle;
    }
    .sub-ratings a[href]:hover {
      background: @crust;
    }

    /* Analysis page */
    .analyse__clock,
    #analyse-cm .title,
    .explorer__config .choices button,
    .mselect__label,
    .cmn-toggle:hover:not(:disabled) + label::after,
    .cmn-toggle + label::after,
    .crosstable povs:hover,
    .crosstable__users,
    .crosstable__score,
    .mselect__list,
    .analyse__tools .comp-off__hint,
    .ceval,
    .explorer-box tr:nth-child(even),
    .board-editor__tools .metadata,
    .mchat__content,
    .mchat__tab-active,
    .crosstable fill {
      background: @crust !important;
    }
    .analyse__tools,
    .copyable,
    .pv_box,
    #ceval-settings {
      background: @mantle;
    }
    .ceval .settings-gear.active {
      background-color: @accent;
    }
    #ceval-settings {
      border-top-color: @accent;
    }
    .mchat__tab:hover {
      background: fade(@accent, 50%);
    }
    .ceval .bar span {
      background: @green;

      &.threat {
        background: @red;
      }
    }
    .show-threat.active,
    .show-threat:hover:not(.hidden) {
      color: @red;
    }
    /* Openings */
    .opening__config,
    .opening__next {
      background: @crust;
    }
    .opening__next {
      outline-color: @base;
    }
    .opening__next:hover {
      outline-color: @accent;
    }
    .opening__next__popularity span {
      background: @accent;
    }
    .opening__next__title {
      color: @accent;
    }

    /* Puzzles */
    .tview2-column > index {
      background: @base;
      color: @subtext0 !important;
    }
    .puzzle__feedback {
      background: @base;
    }
    .puzzle__tools,
    .puzzle__side__metas,
    .puzzle__side__user,
    .puzzle__side__config,
    .puzzle__side__theme {
      background: @mantle;
    }
    input,
    textarea,
    select {
      background: @crust;
    }
    .cmn-toggle:checked + label {
      background-color: @green;
    }
    .cmn-toggle:checked + label::before {
      color: @green;
    }

    /* Teams */
    .slist tbody tr:nth-child(even),
    .team-show__desc,
    .team-show__forum__post:nth-child(odd) {
      background: @crust;
    }

    /* Settings */
    .account .radio input:checked + label,
    .account table.allows tr:nth-child(odd) td {
      background: @crust;
    }
    .radio label {
      background: @base;
    }
    .flash-warning {
      background: @peach;
    }
    .form-control:invalid {
      border-color: @red;
    }
  }
}

/* ==UserStyle==
@name Google Catppuccin
@namespace github.com/catppuccin/userstyles/styles/google
@homepageURL https://github.com/catppuccin/userstyles/tree/main/styles/google
@version 2025.09.06
@updateURL https://github.com/catppuccin/userstyles/raw/main/styles/google/catppuccin.user.less
@supportURL https://github.com/catppuccin/userstyles/issues?q=is%3Aopen+is%3Aissue+label%3Agoogle
@description Soothing pastel theme for Google
@author Catppuccin
@license MIT

@preprocessor less
@var select lightFlavor "Light Flavor" ["latte:Latte*", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha"]
@var select darkFlavor "Dark Flavor" ["latte:Latte", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha*"]
@var select accentColor "Accent" ["rosewater:Rosewater", "flamingo:Flamingo", "pink:Pink", "mauve:Mauve*", "red:Red", "maroon:Maroon", "peach:Peach", "yellow:Yellow", "green:Green", "teal:Teal", "blue:Blue", "sapphire:Sapphire", "sky:Sky", "lavender:Lavender", "subtext0:Gray"]
==/UserStyle== */

@import "https://userstyles.catppuccin.com/lib/lib.less";

@-moz-document regexp("^https?://(www\\.|images\\.)?google\\..*") {
  :root {
    @media (prefers-color-scheme: light) {
      #catppuccin(@lightFlavor);
    }
    @media (prefers-color-scheme: dark) {
      #catppuccin(@darkFlavor);
    }
  }

  #catppuccin(@flavor) {
    #lib.palette();
    #lib.defaults();

    // Unset color-scheme from `#lib.defaults()`.
    iframe {
      color-scheme: none !important;
    }

    &,
    body {
      background: @base !important;
      --uv-styles-color-surface: @crust;
      --uv-styles-color-tertiary: @surface0;
      --uv-styles-color-secondary: fade(@blue, 25%);
      --uv-styles-color-primary: @base;
      --uv-styles-color-on-primary: @blue;
      --uv-styles-color-text-primary: @blue;
      --uv-styles-color-icon-default: @text;
      --uv-styles-color-text-emphasis: @text;
      --uv-styles-color-icon-on-secondary: @subtext0;
      --uv-styles-color-on-tertiary: @text;
      --uv-styles-color-icon-on-tertiary: @text;
      --uv-styles-color-scrim: fade(@crust, 75%);
      --uv-styles-color-on-scrim: @text;
      --uv-styles-color-icon-on-scrim: @text;
      --xhUGwc: @base;
      --bbQxAb: @text;
