    }

    .dcg-blue-link,
    .dcg-footer-link,
    .dcg-primary-link,
    .dcg-shared-tab-gray-underline,
    .dcg-btn-blue-outline {
      color: @accent !important;
    }

    .dcg-footer-legal-content-container,
    .sticky-nav li.active a,
    .dcg-principles-header,
    .dcg-for-work-hero__label,
    .accessibility-main details[open] summary {
      background: @accent !important;
    }

    .dcg-finalist-location, .dcg-subtitle, .breadcrumbs li::before {
      color: @subtext0 !important;
    }

    .dcg-shared-tab-gray-underline.dcg-hovered {
      border-bottom-color: @accent !important;
    }

    .dcg-footer-main-content-container,
    .careers-hero,
    .dcg-background--light-blue,
    .state-info:not(.modal-view) {
      background: @base !important;
    }

    .dcg-footer-legal-info a,
    .dcg-footer-title,
    .dcg-copyright,
    .dcg-footer-legal-info,
    .sticky-nav li.active a,
    .dcg-principles-header * {
      color: @base !important;
    }

    .dcg-test-blurb,
    .dcg-key-command,
    pre,
    .dcg-container,
    .lt-dropdown-menu,
    .accessibility-main details {
      border-color: @surface0 !important;
    }

    .dcg-search-container input,
    .dcg-searchable-dropdown-list,
    .sticky-nav li:not(.active) a,
    .dcg-key-command,
    .dcg-btn-blue-outline,
    *:not(pre) > code {
      background: @mantle !important;
    }

    .dcg-search-container input:focus,
    .pdf-button,
    .dcg-btn-blue-outline,
    .callout--primary,
    .lt-btn,
    .lt-section-subscribe button {
      border-color: @accent !important;
    }

    .dividing-line {
      background: @surface0 !important;
    }

    .job-openings-message-container .white-background {
      background: transparent !important;
    }

    .pdf-button.dcg-hovered, .dcg-btn-blue-outline.dcg-hovered {
      box-shadow: 0 0 0 1px @accent !important;
    }

    .dcg-footer-logo img, .dcg-testimonial__image {
      filter: @text-filter;
    }

    .dcg-for-work-hero__input {
      border-color: @text !important;

      &:focus {
        box-shadow: 0 0 0 1px @text !important;
      }
    }

    .dcg-basic-markdown-container table {
      th, td {
        border-color: @text !important;
      }
    }

    /* Scientific, Geometric, and Matrix */
    .dcg-calc-basic-main, #main {
      --dcg-custom-background-color: @base;
      border-color: transparent !important;
    }

    .dcg-basic-list-container, .dcg-matrix-list-container {
      border-top-color: @surface0 !important;
    }

    .dcg-basic-list-placeholder,
    .dcg-keypad-control-bar,
    .dcg-matrix-list-placeholder {
      border-left-color: @surface0 !important;
      border-right-color: @surface0 !important;
    }

    .dcg-basic-keypad-container,
    .dcg-basic-expression,
    .dcg-latex-expression,
    .dcg-matrix-expression {
      border-color: @surface0 !important;
    }

    .dcg-keypad-control-btn.dcg-selected::after,
    .dcg-segmented-control-btn.dcg-selected {
      background: @accent !important;
    }

    .dcg-basic-expression.dcg-focused,
    .dcg-matrix-expression.dcg-focused,
    .dcg-latex-expression.dcg-focused {
      border-color: @accent !important;
      box-shadow: inset 0 0 0 1px @accent !important;
    }

    .dcg-resize-action {
      background: @mantle !important;
      border-color: @surface0 !important;
    }

    .dcg-matrix-container, .dcg-segmented-control-btn.dcg-selected {
      border-color: @text !important;
    }

    .dcg-matrix-view .dcg-mq-math-mode.dcg-mq-focused {
      border-bottom-color: @accent !important;
    }

    .dcg-mq-ans {
      border-color: @accent !important;
      color: @accent !important;
      background: fade(@accent, 26%) !important;

      &::after {
        border-color: @accent !important;
        color: @accent !important;
        background: @mantle !important;
      }
    }

    .dcg-keypad-control-btn.dcg-disabled {
      opacity: 1 !important;
      color: @surface1 !important;

      i {
        color: @surface1 !important;
      }
    }

    --dcg-custom-text-color-shaded: @text;

    /* Help Center */
    .lt-container-inner *,
    .lt-topbar,
    .hc-multiselect-toggle li span[aria-label]::before {
      color: @text !important;
    }

    .lt-container-inner a:not(.lt-btn--primary), .lt-topbar__link {
      color: @accent !important;
    }

    .lt-topbar {
      background: @crust !important;
    }

    .lt-topbar__logo {
      filter: @text-filter;
    }

    .lt-hero-unit__bg,
    .lt-footer-submit-ticket,
    .tab,
    .lt-new-request-tip,
    .form-field input,
    .form-field textarea,
    .hc-multiselect-toggle,
    .hc-multiselect-menu {
      background: @mantle !important;
    }

    input[type="submit"], .lt-block-list-item__link, .lt-btn--primary {
      background: @accent !important;
      border-color: @accent !important;
      color: @base !important;
    }

    input[type="search"] {
      background: @crust !important;
      border-color: @text !important;
    }

    .search::before {
      @svg: escape(
        '<svg aria-hidden="true" focusable="false" data-prefix="far" data-icon="search" role="img" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 512 512" class="svg-inline--fa fa-search fa-w-16 fa-3x"><path fill="@{text}" d="M508.5 468.9 387.1 347.5c-2.3-2.3-5.3-3.5-8.5-3.5h-13.2c31.5-36.5 50.6-84 50.6-136C416 93.1 322.9 0 208 0S0 93.1 0 208s93.1 208 208 208c52 0 99.5-19.1 136-50.6v13.2c0 3.2 1.3 6.2 3.5 8.5l121.4 121.4c4.7 4.7 12.3 4.7 17 0l22.6-22.6c4.7-4.7 4.7-12.3 0-17M208 368c-88.4 0-160-71.6-160-160S119.6 48 208 48s160 71.6 160 160-71.6 160-160 160" class=""/></svg>'
      );
      background-image: url("data:image/svg+xml,@{svg}");
    }

    .lt-block-list-item__link * {
      color: @base !important;
    }

    .lt-custom-block__icon {
      filter: @accent-filter;
    }

    .lt-footer,
    .lt-search-box:not(.lt-search-box--hero-unit),
    .lt-breadcrumbs-wrapper,
    .hc-multiselect-menu li:focus,
    .hc-multiselect-toggle li {
      background: @base !important;
    }

    .lt-scroll-to-top {
      color: @accent !important;
    }

    .pagination-list *, .lt-scroll-to-top, .lt-new-request-tip {
      border-color: @accent !important;
    }

    .lt-header, .lt-search-result {
      border-bottom-color: @surface0 !important;
    }

    .lt-search-result__text em {
      background: @yellow !important;
      color: @base !important;
    }

    .lt-toc--link.is-active > span {
      color: @base !important;
    }

    .tab, .key-command, .hc-multiselect-toggle, .hc-multiselect-menu {
      border-color: @surface0 !important;
    }

    @layer {
      .tabs-link {
        color: @text !important;

        &:hover {
          color: @accent !important;
        }

        &.is-active {
          color: @base !important;
          background: @accent !important;
          border-color: @accent !important;
        }
      }
    }

    .upload-dropzone[data-hc-focus="true"] {
      border-color: @accent !important;
    }

    --input-border-color: @surface0;
    --input-focus-border-color: @accent;

    /* Blog */
    .content-wrap *:not(a), .tag-links > a {
      color: @text !important;
    }

    .article header {
      border-top-color: @surface0 !important;
    }

    *:not(.tag-links) > a:not(.img-card, .btn-blue, .btn-blue-outline) {
      color: @accent;
    }

    .tag-container a {
      border-color: @text !important;

      &:active {
        background: fade(@text, 50%) !important;
      }
    }

    .img-card {
      border-color: @surface2 !important;

      &:hover {
        box-shadow: 0 0 0 1px @surface2 !important;
      }
    }

    .btn-blue {
      background: @accent !important;
      color: @base !important;
    }

    .btn-blue-outline {
      border-color: @accent !important;
      color: @accent !important;

      &:hover {
        box-shadow: 0 0 0 1px @accent !important;
      }
    }
  }
}

/* ==UserStyle==
@name crates.io Catppuccin
@namespace github.com/catppuccin/userstyles/styles/crates.io
@homepageURL https://github.com/catppuccin/userstyles/tree/main/styles/crates.io
@version 2025.09.06
@updateURL https://github.com/catppuccin/userstyles/raw/main/styles/crates.io/catppuccin.user.less
@supportURL https://github.com/catppuccin/userstyles/issues?q=is%3Aopen+is%3Aissue+label%3Acrates.io
@description Soothing pastel theme for crates.io
@author Catppuccin
@license MIT

@preprocessor less
@var select lightFlavor "Light Flavor" ["latte:Latte*", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha"]
@var select darkFlavor "Dark Flavor" ["latte:Latte", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha*"]
@var select accentColor "Accent" ["rosewater:Rosewater", "flamingo:Flamingo", "pink:Pink", "mauve:Mauve*", "red:Red", "maroon:Maroon", "peach:Peach", "yellow:Yellow", "green:Green", "teal:Teal", "blue:Blue", "sapphire:Sapphire", "sky:Sky", "lavender:Lavender", "subtext0:Gray"]
==/UserStyle== */

@import "https://userstyles.catppuccin.com/lib/lib.less";

@-moz-document domain("crates.io") {
  @import url("https://unpkg.com/@catppuccin/highlightjs@1.0.0/css/catppuccin-variables.important.css");

  code.hljs {
    background: none !important;
  }

  :root[data-color-scheme="system"] {
    @media (prefers-color-scheme: light) {
      #catppuccin(@lightFlavor);
    }
    @media (prefers-color-scheme: dark) {
      #catppuccin(@darkFlavor);
    }
  }

  :root[data-color-scheme="dark"] {
    #catppuccin(@darkFlavor);
  }
  :root[data-color-scheme="light"] {
    #catppuccin(@lightFlavor);
  }

  #catppuccin(@flavor) {
    #lib.palette();
    #lib.defaults();
    #lib.css-variables();

    &,
    * {
      --shadow: none !important;
      --csstools-color-scheme--dark: none;

      --header-bg-color: @mantle;
      --main-color: @text;
      --main-color-light: @subtext1;
      --main-shadow-color: @surface0;
      --main-bg: @mantle;
      --main-bg-dark: @base;
      --gray-border: @surface0;
      --link-color: @accent;
      --link-hover-color: darken(@accent, 5%);
      --footer-header-color: @text;

      /* Main article/readme background */
      --csstools-light-dark-toggle--63: @base;

      /* Inline code background */
      --csstools-light-dark-toggle--92: @mantle;
      /* Code block background */
      --csstools-light-dark-toggle--91: @mantle;
      /* Copyable text hover */
      --csstools-light-dark-toggle--78: @base;

      /* Versions cards */
      --csstools-light-dark-toggle--111: @text;
      --csstools-light-dark-toggle--113: @base;
      --csstools-light-dark-toggle--114: fade(@green, 15%);
      --csstools-light-dark-toggle--117: fade(@yellow, 15%);
      --csstools-light-dark-toggle--109: @surface0;
      --csstools-light-dark-toggle--110: @surface0;
      --csstools-light-dark-toggle--115: @surface0;
      --csstools-light-dark-toggle--118: @surface0;
      --csstools-light-dark-toggle--126: @text;
      [class*="_new"] {
        color: @green;
      }
      /* Dependencies cards */
      --csstools-light-dark-toggle--83: @base;
      --csstools-light-dark-toggle--79: @surface0;
      --csstools-light-dark-toggle--80: @text;
      --csstools-light-dark-toggle--81: @text;
      --csstools-light-dark-toggle--84: @subtext0;
      --grey600: @subtext0;
      --grey700: @text;
      /* Dependents cards */
      --csstools-light-dark-toggle--89: @base;
      --csstools-light-dark-toggle--86: @surface0;
      /* Search results cards */
      --csstools-light-dark-toggle--2: @base;
      /* Homepage cards */
      --csstools-light-dark-toggle--74: @base;
      --csstools-light-dark-toggle--77: @surface0;
      --csstools-light-dark-toggle--75: @subtext1;
      --csstools-light-dark-toggle--76: @text;
      --csstools-light-dark-toggle--19: @subtext0;
      --csstools-light-dark-toggle--20: @subtext0;

      /* Header search bar */
      --csstools-light-dark-toggle--34: @crust;
      --yellow500: @accent;
      --yellow700: darken(@accent, 5%);

      /* Dropdown */
      --csstools-light-dark-toggle--23: @mantle;
      --csstools-light-dark-toggle--35: @mantle;
      /* Dropdown items */
      [class*="_item_"] {
        button:hover,
        > a:hover,
        [class*="_selected_"] {
          background: @surface0;
          color: @text !important;
        }
      }
    }

    /* Toasts */
    .c-notification {
      --ecn-green: @green;
      --ecn-blue: @blue;
      --ecn-orange: @peach;
      --ecn-red: @red;

      &,
      .c-notification__icon {
        color: @crust;
      }
    }

    /* Tooltips */
    .ember-tooltip {
      color: @text;
      background-color: @crust;
      text-shadow: none;

      &[x-placement^="top"] .ember-tooltip-arrow {
        border-top-color: @crust;
      }
    }

    /* Icons and icon badges */
    [class*="downloads_"] [fill="#FFF"] {
      fill: @surface0 !important;
    }
    [fill="#B13B89"],
    [fill="#B84D94"],
    svg[class*="_download-icon"] {
      fill: @accent !important;
    }
    [stroke="#B84D94"] {
      stroke: @accent !important;
    }

    /* Footer links */
    ._content_hautyr {
      h1 {
        text-shadow: none;
      }

      a {
        color: @accent;

        &:hover {
          color: darken(@accent, 5%);
          text-shadow: none;
        }
      }
    }

    /* Misc header text */
    [class*="_header-inner_"] {
      &,
      a {
        color: @text;
      }
    }

    /* Search bar magnifying glass icon */
    form[action="/search"] button {
      color: @crust;
    }

    /* Sort by search results */
    ._icon_xsqxju {
      color: @accent;
    }

    /* Primary buttons */
    [class*="_tan-button_"],
    [class*="_yellow-button_"] {
      color: @crust !important;
      background: @accent;

      &:hover {
        background-color: darken(@accent, 5%);
      }
    }

    [class*="_red-button_"] {
      --bg-color-top: @maroon;
      --bg-color-bottom: @maroon;
      --bg-color-top-dark: @red;
      --bg-color-bottom-dark: @red;
    }

    /* User email settings */
    [class*="_me-email_"] {
      [class*="_row_"] {
        border-color: @surface0;
      }
      [class*="_verified_"] {
        color: @green;
      }
    }

    /* Logged out home page hero */
    [class*="_hero-title_"] {
      color: @text;
      text-shadow: none;
    }
    [class*="_hero-button_"] [class*="_icon_"] {
      color: @crust;
    }
    [class*="_blurb_"] [class*="_stats-value_"] {
      [class*="_label_"],
      svg {
        color: @subtext0;
      }
    }

    /* User avatar backgrounds */
    [class*="_avatar_"] {
      background-color: @surface0;
      box-shadow: none;
    }

    [class*="_yanked-badge_"] {
      background-color: @red;
      color: @crust;
    }

    /* Tables in READMEs */
    table {
      th,
      td {
        border-color: @surface1;
      }
    }
  }
}

/* ==UserStyle==
@name docs.rs Catppuccin
